function updateBitcoinPrice(t="USD"){fetch("https://api.coinbase.com/v2/prices/BTC-USD/spot").then(t=>t.json()).then(e=>{const i=parseFloat(e.data.amount);let c=i;"GBP"===t?c*=.8:"EUR"===t&&(c*=.9);const n=Math.round(1e4*c);document.getElementById("multiplied-price").textContent=`${n.toLocaleString("en-US",{style:"currency",currency:t,minimumFractionDigits:0,maximumFractionDigits:0})}`}).catch(t=>{console.error("Error fetching Bitcoin price:",t),document.getElementById("multiplied-price").textContent="Error fetching Bitcoin price."})}updateBitcoinPrice();const buttons=document.querySelectorAll(".btn-group button");buttons.forEach(t=>{t.addEventListener("click",()=>{buttons.forEach(t=>t.classList.remove("active")),t.classList.add("active"),updateBitcoinPrice(t.id.split("-")[0].toUpperCase())})});